sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	if ( g_form.getValue('milestone_completion_date') === ''){
		g_form.removeDecoration('milestone_completion_date','icon-check','Task is done.','color-green');
	} else {
		g_form.addDecoration('milestone_completion_date','icon-check','Task is done.','color-green');
	}
	if ( g_form.getValue('milestone2_completion_date') === ''){
		g_form.removeDecoration('milestone_completion_date','icon-check','Task is done.','color-green');
	} else {
		g_form.addDecoration('milestone2_completion_date','icon-check','Task is done.','color-green');
	}
	if ( g_form.getValue('milestone3_completion_date') === ''){
		g_form.removeDecoration('milestone3_completion_date','icon-check','Task is done.','color-green');
	} else {
		g_form.addDecoration('milestone3_completion_date','icon-check','Task is done.','color-green');
	}   
}
